import React from 'react';
import { Line, Bar } from 'react-chartjs-2';

export default function PerformanceMetrics() {
  const battingData = {
    labels: ['Match 1', 'Match 2', 'Match 3', 'Match 4', 'Match 5'],
    datasets: [{
      label: 'Runs Scored',
      data: [45, 78, 32, 90, 65],
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  const bowlingData = {
    labels: ['Match 1', 'Match 2', 'Match 3', 'Match 4', 'Match 5'],
    datasets: [{
      label: 'Wickets Taken',
      data: [2, 1, 3, 0, 2],
      backgroundColor: 'rgba(153, 102, 255, 0.2)',
      borderColor: 'rgb(153, 102, 255)',
      borderWidth: 1
    }]
  };

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold text-gray-800">Performance Metrics</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Batting Performance</h2>
          <Line data={battingData} />
        </div>

        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Bowling Performance</h2>
          <Bar data={bowlingData} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-semibold mb-4">Performance Zones</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {['Powerplay', 'Middle Overs', 'Death Overs', 'Overall'].map((zone) => (
            <div key={zone} className="p-4 border rounded-lg">
              <h3 className="font-medium text-gray-800">{zone}</h3>
              <div className="mt-2">
                <div className="h-2 bg-blue-200 rounded">
                  <div
                    className="h-2 bg-blue-500 rounded"
                    style={{ width: `${Math.random() * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
